// Utiliser child_process pour exÃ©cuter les commandes
const { execSync } = require('child_process');

try {
  // RedÃ©marrer PM2
  const restartOutput = execSync('cd /root/frankito-bot && pm2 restart all', {
    encoding: 'utf-8',
    timeout: 10000
  });

  // Obtenir le statut
  const statusOutput = execSync('pm2 status', {
    encoding: 'utf-8',
    timeout: 5000
  });

  return {
    success: true,
    restart_output: restartOutput,
    status: statusOutput,
    timestamp: new Date().toISOString()
  };
} catch (error) {
  return {
    success: false,
    error: error.message,
    stderr: error.stderr?.toString(),
    timestamp: new Date().toISOString()
  };
}
